# Define class Automobile
class Automobile():
    def __init__(self,ndoors=0,clr=""):
        self._doors = ndoors
        self._colour = clr

    def printDoors(self):
        return "Number of doors is: "+str(self._doors)

    def printColour(self):
        return "Colour is: "+self._colour

    def display(self):
        return "Car is: "+str(self._doors)+" doors,"+self._colour

